
public class FormatString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int arg = 20;
		double tall = 175.8;
		String name = "홍자바";
		
		System.out.printf("제 이름 %s 입니다. \n", name);
		System.out.printf("니이는 %d이고 , 키는 %e 입니다. \n", arg, tall);
		System.out.printf("%d %o %x \n", 77, 77,77 ,77);
		System.out.printf("%g %g \n", 0.114, 0.114);
	}

}
